  <style>
    :root {
      --bg: #060608;
      --surface: #0d0d10;
      --border: #1a1a20;
      --text: #d4d4d8;
      --secondary: #71717a;
      --dim: #3f3f46;
      --green: #4ade80;
      --amber: #fbbf24;
      --blue: #93c5fd;
      --red: #fca5a5;
      --purple: #c4b5fd;
      --font: 'IBM Plex Mono', monospace;
    }
    * { margin: 0; padding: 0; box-sizing: border-box; }
    body {
      background: var(--bg);
      color: var(--text);
      font-family: var(--font);
      font-size: 13px;
      line-height: 1.7;
      -webkit-font-smoothing: antialiased;
    }
    a { color: var(--green); text-decoration: none; }
    a:hover { text-decoration: underline; }
    ::selection { background: rgba(74,222,128,0.15); }

    .page { max-width: 960px; margin: 0 auto; padding: 0 24px; }

    /* header */
    .header {
      padding: 32px 0;
      border-bottom: 1px solid var(--border);
      display: flex;
      justify-content: space-between;
      align-items: center;
    }
    .header-left { display: flex; align-items: center; gap: 16px; }
    .header h1 { font-size: 20px; font-weight: 400; letter-spacing: 1px; }
    .header-links { display: flex; gap: 16px; font-size: 12px; }
    .header-links a { color: var(--secondary); }
    .header-links a:hover { color: var(--text); text-decoration: none; }

    /* hero */
    .hero {
      padding: 48px 0;
      border-bottom: 1px solid var(--border);
    }
    .hero h2 {
      font-size: 16px;
      font-weight: 400;
      color: var(--text);
      margin-bottom: 12px;
    }
    .hero p {
      color: var(--secondary);
      font-size: 13px;
      max-width: 600px;
      line-height: 1.8;
    }
    .hero .highlight {
      color: var(--green);
      font-weight: 500;
    }

    /* services */
    .services {
      padding: 32px 0;
    }
    .services-title {
      font-size: 12px;
      color: var(--dim);
      text-transform: uppercase;
      letter-spacing: 2px;
      margin-bottom: 24px;
    }
    .service-card {
      background: var(--surface);
      border: 1px solid var(--border);
      border-radius: 8px;
      padding: 24px;
      margin-bottom: 16px;
    }
    .service-header {
      display: flex;
      justify-content: space-between;
      align-items: flex-start;
      margin-bottom: 12px;
    }
    .service-name {
      font-size: 15px;
      font-weight: 500;
      color: var(--text);
    }
    .service-price {
      font-size: 14px;
      color: var(--green);
      font-weight: 500;
    }
    .service-desc {
      color: var(--secondary);
      font-size: 12px;
      line-height: 1.8;
      margin-bottom: 16px;
    }
    .service-actions {
      display: flex;
      gap: 12px;
      align-items: center;
    }
    .btn {
      background: transparent;
      border: 1px solid var(--border);
      color: var(--text);
      padding: 8px 16px;
      border-radius: 4px;
      font-family: var(--font);
      font-size: 12px;
      cursor: pointer;
      transition: all 0.2s;
    }
    .btn:hover {
      border-color: var(--green);
      color: var(--green);
    }
    .btn-primary {
      background: var(--green);
      border-color: var(--green);
      color: var(--bg);
    }
    .btn-primary:hover {
      background: #22c55e;
      border-color: #22c55e;
      color: var(--bg);
    }
    .service-note {
      color: var(--dim);
      font-size: 11px;
    }

    /* how it works */
    .how-it-works {
      padding: 32px 0;
      border-top: 1px solid var(--border);
    }
    .how-it-works h3 {
      font-size: 12px;
      color: var(--dim);
      text-transform: uppercase;
      letter-spacing: 2px;
      margin-bottom: 24px;
    }
    .steps {
      display: grid;
      grid-template-columns: repeat(3, 1fr);
      gap: 24px;
    }
    .step {
      background: var(--surface);
      border: 1px solid var(--border);
      border-radius: 8px;
      padding: 20px;
    }
    .step-num {
      font-size: 24px;
      color: var(--dim);
      margin-bottom: 8px;
    }
    .step-title {
      font-size: 13px;
      font-weight: 500;
      margin-bottom: 8px;
    }
    .step-desc {
      color: var(--secondary);
      font-size: 12px;
      line-height: 1.6;
    }

