    .payment-info {
      padding: 32px 0;
      border-top: 1px solid var(--border);
    }
    .payment-info h3 {
      font-size: 12px;
      color: var(--dim);
      text-transform: uppercase;
      letter-spacing: 2px;
      margin-bottom: 16px;
    }
    .payment-info p {
      color: var(--secondary);
      font-size: 12px;
      line-height: 1.8;
    }
    .payment-info code {
      background: var(--surface);
      padding: 2px 6px;
      border-radius: 3px;
      font-size: 11px;
      color: var(--green);
