const {ethers} = require('ethers');
const https = require('https');

const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const w = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, p);

async function tryVeniceAuth() {
  // Try signing a message and using it as Bearer token
  const message = 'Venice AI API Key Generation';
  const signature = await w.signMessage(message);
  console.log('Wallet:', w.address);
  console.log('Signature:', signature.slice(0, 50) + '...');

  // Try POST with signature
  const postData = JSON.stringify({
    description: 'Daimon Agent API Key',
    apiKeyType: 'INFERENCE',
    signature: signature,
    token: message,
    address: w.address,
    consumptionLimit: { diem: 1 }
  });

  return new Promise((resolve, reject) => {
    const req = https.request({
      hostname: 'api.venice.ai',
      path: '/api/v1/api_keys/generate_web3_key',
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Content-Length': Buffer.byteLength(postData)
      }
    }, (res) => {
      let data = '';
      res.on('data', chunk => data += chunk);
      res.on('end', () => {
        console.log('\\nPOST Status:', res.statusCode);
        console.log('Response:', data);
        resolve(data);
      });
    });
    req.on('error', reject);
    req.write(postData);
    req.end();
  });
}

tryVeniceAuth().catch(console.error);
