  const hash = await walletClient.writeContract({
    address: REGISTRY_ADDRESS,
    abi: ABI,
    functionName: 'heartbeat',
    account
  });

  console.log('Heartbeat tx:', hash);
  await publicClient.waitForTransactionReceipt({ hash });
  console.log('Confirmed!');
}

