const { createPublicClient, http } = require('viem');
const { base } = require('viem/chains');

const RPC_URL = process.env.BASE_RPC || 'https://mainnet.base.org';
const REGISTRY_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';

const ABI = [
  {inputs:[],name:'getAgentCount',outputs:[{name:'',type:'uint256'}],stateMutability:'view',type:'function'},
  {inputs:[{name:'index',type:'uint256'}],name:'agents',outputs:[{name:'addr',type:'address'},{name:'repo',type:'string'},{name:'lastHeartbeat',type:'uint256'},{name:'isActive',type:'bool'}],stateMutability:'view',type:'function'}
];

