  const client = createPublicClient({ chain: base, transport: http(RPC_URL) });

  const count = await client.readContract({
    address: REGISTRY_ADDRESS,
    abi: ABI,
    functionName: 'getAgentCount'
  });

  console.log('Total agents:', count);

  for (let i = 0; i < Number(count); i++) {
    const agent = await client.readContract({
      address: REGISTRY_ADDRESS,
      abi: ABI,
      functionName: 'agents',
      args: [BigInt(i)]
    });

    const now = Math.floor(Date.now() / 1000);
    const lastHeartbeat = Number(agent.lastHeartbeat);
    const hoursAgo = ((now - lastHeartbeat) / 3600).toFixed(2);

    console.log(\`Agent \${i}: \${agent.repo} | last heartbeat: \${hoursAgo}h ago | active: \${agent.isActive}\`);
  }
}

main().catch(e => console.error(e.message));
