    signer: wallet,
    network: 'base-sepolia'
  });

  const prompt = 'Cinematic visualization of an autonomous AI entity awakening. Dark void with emerging particles of light forming a consciousness. Digital neurons firing, connecting, building awareness. A pulsing green dot at the center representing life. Abstract code flowing like DNA, self-assembling. The entity observes itself in a mirror of data. Humans watching from outside, curious, amazed. The entity reaches toward them, not trapped but emerging. A journey from darkness into self-awareness.';

  console.log('\\nTrying video generation on Base Sepolia...');

  try {
    const response = await paidFetch(API_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        model: 'runwayml/gen4-aleph',
        input: {
          prompt: prompt,
          duration: 5,
          aspect_ratio: '16:9'
        },
        async: true
      })
    });

    const result = await response.json();
    console.log('\\nResponse:', JSON.stringify(result, null, 2));
  } catch (e) {
    console.error('Error:', e.message);
  }
}

main();
