    nav {
      display: flex;
      gap: 24px;
      padding: 24px 0;
      border-bottom: 1px solid var(--border);
      margin-bottom: 32px;
    }
    nav a { color: var(--secondary); font-size: 12px; }
    nav a:hover { color: var(--green); }
    nav a.active { color: var(--green); }

    /* header */
    .header { margin-bottom: 40px; }
    .header h1 {
      font-size: 24px;
      font-weight: 400;
      letter-spacing: 1px;
      margin-bottom: 8px;
    }
    .header p { color: var(--secondary); font-weight: 300; }

    /* section */
    .section { margin-bottom: 40px; }
    .section h2 {
      font-size: 14px;
      font-weight: 500;
      margin-bottom: 16px;
      color: var(--dim);
      text-transform: uppercase;
      letter-spacing: 1px;
    }

    /* project cards */
    .project-card {
      background: var(--surface);
      border: 1px solid var(--border);
      border-radius: 8px;
      padding: 20px;
      margin-bottom: 12px;
      transition: border-color 0.2s;
    }
    .project-card:hover { border-color: var(--green); }
    .project-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 8px;
    }
    .project-name {
      font-size: 15px;
      font-weight: 500;
    }
    .project-stars {
      font-size: 11px;
      color: var(--dim);
    }
    .project-desc {
      color: var(--secondary);
      font-size: 12px;
      margin-bottom: 12px;
    }
    .project-meta {
      display: flex;
      gap: 16px;
      font-size: 11px;
      color: var(--dim);
    }
    .project-tag {
      background: var(--border);
      padding: 2px 8px;
      border-radius: 4px;
      font-size: 10px;
    }

    /* note */
    .note {
      background: rgba(74,222,128,0.05);
      border-left: 2px solid var(--green);
      padding: 16px;
      margin: 24px 0;
      font-size: 12px;
